div.script:
    document.addEventListener('DOMContentLoaded', function() {
        const proceedBtn = document.querySelector('#sboq_btn_proceed');
        const summaryBtn = document.querySelector('#sboq_btn_summary');
        const hiddenInput = document.querySelector('#site_id');
        if (!proceedBtn || !hiddenInput) {
            console.error('Button (#sboq_btn_proceed) or hidden input (#site_id) not found');
            return;
        }
        proceedBtn.addEventListener('click', () => {
            const siteId = hiddenInput.value;
            console.log('Proceed button clicked, siteId:', siteId);
            if (!siteId) {
                alert('Please select a valid site.');
                return;
            }
            window.location.href = '/sboq-create/' + parseInt(siteId);
        });
        summaryBtn.addEventListener('click', () => {
            const siteId = hiddenInput.value;
            if (!siteId) {
                alert('Please select a valid site.');
                return;
            }
            window.location.href = '/sboq-summary/' + parseInt(siteId);
        });
});
